import { App } from 'ionic-angular';
import { Component } from '@angular/core';
//import { IonicPage, NavController, NavParams } from 'ionic-angular';
import { AuthService } from '../../providers/auth-service/auth-service';
import { Storage } from "@ionic/storage";
import { Globals } from '../../providers/globals';
import { LoginPage } from '../login/login';
import { Camera, CameraOptions } from '@ionic-native/camera';
import { IonicPage, NavController, NavParams, ActionSheetController, ToastController, Platform, LoadingController, Loading } from 'ionic-angular';
 
import { File } from '@ionic-native/file';
import { Transfer, TransferObject } from '@ionic-native/transfer';
import { FilePath } from '@ionic-native/file-path';
/**
 * Generated class for the MainPage page.
 *
 * See http://ionicframework.com/docs/components/#navigation for more info
 * on Ionic pages and navigation.
 */


declare var cordova: any;

@IonicPage()
@Component({
  selector: 'page-main',
  templateUrl: 'main.html',
  providers: [Camera]
})
export class MainPage {
  lastImage: string = null;
  loading: Loading;
  username = '';
  email = '';
  public base64Image: string;
  constructor(private app: App, public nav: NavController, public navParams: NavParams, private auth: AuthService,private camera: Camera, private storage: Storage, public globals: Globals, private transfer: Transfer, private file: File, private filePath: FilePath, public actionSheetCtrl: ActionSheetController, public toastCtrl: ToastController, public platform: Platform, public loadingCtrl: LoadingController) {
    let info = this.auth.getUserInfo();
    this.username = info['name'];
    this.email = info['email'];

    this.getToken();
  }


  public getToken() {
    return new Promise((resolve, reject) => {
        this.storage.get('token').then((value) => {
            //this.token = value;
            this.globals.setGlobalToken(value);
            resolve(value);
        });
    });
  }

  public logout() {
    this.storage.remove('token');
    this.auth.logout().subscribe(succ => {
      let nav = this.app.getRootNav(); //getComponent('nav');
      nav.push(LoginPage)
    });
  }
  /*Upload(){
      this.auth.uploadProfileImage("data:image/jpeg;base64,");
  }*/

public presentActionSheet() {
    let actionSheet = this.actionSheetCtrl.create({
      title: 'Select Image Source',
      buttons: [
        {
          text: 'Load from Library',
          handler: () => {
            this.takePicture(this.camera.PictureSourceType.PHOTOLIBRARY);
          }
        },
        {
          text: 'Use Camera',
          handler: () => {
            this.takePicture(this.camera.PictureSourceType.CAMERA);
          }
        },
        {
          text: 'Cancel',
          role: 'cancel'
        }
      ]
    });
    actionSheet.present();
  }


  takePicture(sourceType){
  /*
    const options: CameraOptions = {
      quality: 100,
      destinationType: this.camera.DestinationType.DATA_URL,
      encodingType: this.camera.EncodingType.JPEG,
      mediaType: this.camera.MediaType.PICTURE
    }*/
    //this.camera.PictureSourceType.PHOTOLIBRARY

  this.camera.getPicture({
     // destinationType: this.camera.DestinationType.DATA_URL,
      sourceType: sourceType,
      quality: 60,
      saveToPhotoAlbum: false,
      correctOrientation: true
    }).then((imagePath) => {
    // imageData is a base64 encoded string
     // this.base64Image = "data:image/jpeg;base64," + imageData;
    ////  this.auth.uploadProfileImage(this.base64Image);

 // Special handling for Android library
    if (this.platform.is('android') && sourceType === this.camera.PictureSourceType.PHOTOLIBRARY) {
      this.filePath.resolveNativePath(imagePath)
        .then(filePath => {
          let correctPath = filePath.substr(0, filePath.lastIndexOf('/') + 1);
          let currentName = imagePath.substring(imagePath.lastIndexOf('/') + 1, imagePath.lastIndexOf('?'));
          this.copyFileToLocalDir(correctPath, currentName, this.createFileName());
        });
    } else {
      var currentName = imagePath.substr(imagePath.lastIndexOf('/') + 1);
      var correctPath = imagePath.substr(0, imagePath.lastIndexOf('/') + 1);
      this.copyFileToLocalDir(correctPath, currentName, this.createFileName());
    }

  }, (err) => {
      console.log(err);
  });

}
  ionViewDidLoad() {
    console.log('ionViewDidLoad MainPage');
  }

private createFileName() {
  var d = new Date(),
  n = d.getTime(),
  newFileName =  n + ".jpg";
  return newFileName;
}
 
// Copy the image to a local folder
private copyFileToLocalDir(namePath, currentName, newFileName) {
  this.file.copyFile(namePath, currentName, cordova.file.dataDirectory, newFileName).then(success => {
    this.lastImage = newFileName;
  }, error => {
    this.presentToast('Error while storing file.');
  });
}
 
private presentToast(text) {
  let toast = this.toastCtrl.create({
    message: text,
    duration: 3000,
    position: 'top'
  });
  toast.present();
}
 
// Always get the accurate path to your apps folder
public pathForImage(img) {
  if (img === null) {
    return '';
  } else {
    return cordova.file.dataDirectory + img;
  }
}

public uploadImage() {
  // Destination URL
  var url = "http://orgeventos.growner.com/upload.php";
 
  // File for Upload
  var targetPath = this.pathForImage(this.lastImage);
 
  // File name only
  var filename = this.lastImage;
 
  var options = {
    fileKey: "file",
    fileName: filename,
    chunkedMode: false,
    mimeType: "multipart/form-data",
    params : {'fileName': filename}
  };
 
  const fileTransfer: TransferObject = this.transfer.create();
 
  this.loading = this.loadingCtrl.create({
    content: 'Uploading...',
  });
  this.loading.present();
 
  // Use the FileTransfer to upload the image
  fileTransfer.upload(targetPath, url, options).then(data => {
    this.loading.dismissAll()
    this.presentToast('Image succesful uploaded.');
  }, err => {
    this.loading.dismissAll()
    this.presentToast('Error while uploading file.');
  });
}

}
